    AND b_books.created_date >= #{cond.createdDateB}


    AND b_books.created_date <= #{cond.createdDateE}


    AND b_books.updated_date >= #{cond.updatedDateB}


    AND b_books.updated_date <= #{cond.updatedDateE}

