book_name = #{bookName}, book_img_url = #{bookImgUrl}, book_version = #{bookVersion}, book_issn = #{bookIssn}, book_isbn = #{bookIsbn}, book_asin = #{bookAsin}, book_author = #{bookAuthor}, book_desc = #{bookDesc}, book_price = #{bookPrice}, book_press = #{bookPress}, book_url = #{bookUrl}, book_src = #{bookSrc}, price = #{price}, version = version + 1, updated_date = current_timestamp(), updated_by=#{updatedBy}
