mapper.sql:
WHERE b_order_detail.deleted_flag = 0

    AND b_order_detail.id = #{cond.id}


    AND b_order_detail.order_id = #{cond.orderId}


    AND b_order_detail.user_book_id = #{cond.userBookId}


    AND b_order_detail.version = #{cond.version}


    and b_order_detail.id in

        #{item}



    AND b_order_detail.created_date >= #{cond.createdDateB}


    AND b_order_detail.created_date <= #{cond.createdDateE}


    AND b_order_detail.updated_date >= #{cond.updatedDateB}


    AND b_order_detail.updated_date <= #{cond.updatedDateE}

mapper.insert:
INSERT INTO b_order_detail(
) VALUES
( #{item.orderId}, #{item.userBookId}, 0, current_timestamp(), current_timestamp(), #{item.createdBy}, #{item.updatedBy}, 1 )

mapper.update:
update b_order_detail
order_id = #{orderId}, user_book_id = #{userBookId}, version = version + 1, updated_date = current_timestamp(), updated_by=#{updatedBy}
where b_order_detail.id = #{id}

