mapper.sql:
WHERE b_user_book.deleted_flag = 0

    AND b_user_book.id = #{cond.id}


    AND b_user_book.book_id = #{cond.bookId}


    AND b_user_book.user_id = #{cond.userId}


    AND b_user_book.pick_method = #{cond.pickMethod}


    AND b_user_book.pick_address = #{cond.pickAddress}


    AND b_user_book.version = #{cond.version}


    and b_user_book.id in

        #{item}



    AND b_user_book.created_date >= #{cond.createdDateB}


    AND b_user_book.created_date <= #{cond.createdDateE}


    AND b_user_book.updated_date >= #{cond.updatedDateB}


    AND b_user_book.updated_date <= #{cond.updatedDateE}

mapper.insert:
INSERT INTO b_user_book(
) VALUES
( #{item.bookId}, #{item.userId}, #{item.pickMethod}, #{item.pickAddress}, 0, current_timestamp(), current_timestamp(), #{item.createdBy}, #{item.updatedBy}, 1 )

mapper.update:
update b_user_book
book_id = #{bookId}, user_id = #{userId}, pick_method = #{pickMethod}, pick_address = #{pickAddress}, version = version + 1, updated_date = current_timestamp(), updated_by=#{updatedBy}
where b_user_book.id = #{id}
